import org.junit.Test;
import org.junit.runner.RunWith;
import org.junit.runners.Parameterized;

import java.util.Arrays;
import java.util.Collection;

import test1.Utils;

import static org.junit.Assert.assertEquals;

@RunWith(value = Parameterized.class)
public class UtilsGetCountTest {

    private String firstString;
    private String secondString;
    private int repeats;

    public UtilsGetCountTest(String firstString, String secondString, int repeats) {
        this.firstString = firstString;
        this.secondString = secondString;
        this.repeats = repeats;
    }

    @Parameterized.Parameters
    public static Collection<Object[]> getTestParameters() {
        return Arrays.asList(new Object[][]{
                {"my name is alireza", "alireza", 1},
                {"alireza is alireza ", "alireza", 2},
                {"alireza is Alireza ", "alireza", 1},
                {"alireza is alireza ", "alireza", 2},
                {"alireza is alireza ", "alireza", 2},
                {"","",1},
                {"","a",10000},
                {"i am a student", "alireza", 0}
        });
    }

    @Test(timeout = 50)
    public void testGetCount() {
        Utils utils = new Utils();
        int result = utils.getCount(firstString, secondString);
        assertEquals(repeats, result);
    }
}
